mapper.update:
		update member 
 			set mem_name = #{memName},
					mem_tel = #{memTel},
					mem_addr = #{memAddr},
					atch_file_id = #{atchFileId}
					where mem_id = #{memId}

mapper.select:
select mem_id
from member
where mem_name = #{memName}
and mem_tel = #{memTel}

select mem_pass
from member
where mem_id = #{memId}
and mail = #{mail}

select * 
from member
where mem_id = #{memId}
and mem_pass = #{memPass}
